import axios from 'axios';
import * as ApiUrls from '../Constants/apiUrls';

class ForgotPwdApi {
  static forgotPwd(forgotPwdObj) {
    console.log("forgotPwdObj",forgotPwdObj);
    const reqObj = {
        method: 'POST',
        url: ApiUrls.authservice+'/auth/forgotPassword',
        data: forgotPwdObj
      };
    return axios(reqObj);
  }
}

export default ForgotPwdApi;