import axios from 'axios';
import * as ApiUrls from '../Constants/apiUrls';

class LoginApi {
  static login(loginObj) {
    const reqObj = {
        method: 'POST',
        url: ApiUrls.authservice+'/auth/login',
        data: loginObj
      };
    return axios(reqObj);
  }

  static authenticateService(orgId) {
    const reqObj = {
      method: 'GET',
      url: ApiUrls.authservice + '/auth/getAuthConfig/' + orgId
    }
    return axios(reqObj);
  }
}

export default LoginApi;
