export default function LoginReducer(state={loginResponse:{}, themesResponse:{}}, action){
    switch(action.type){
        case "LOGIN_SUCCESS":
            return Object.assign({}, state, action.loginResponse);

        case "THEMES_SUCCESS":
            return Object.assign({}, state, action.themesResponse);

        case "AUTH_MODE_SUCCESS":
            let authModeResponse = {
              isADFSenabled: action.response.ADFSRequired,
              domain: action.response.domain,
              graphApiUri: action.response.graphApiUri
            }
            return Object.assign({}, state, {authModeResponse: authModeResponse})

        default:
            return state;
    }
}
