import axios from 'axios';

import React, { PropTypes } from 'react';
import { Link } from 'react-router-dom';
import ReactDOM from 'react-dom';
import * as ApiUrls from '../Constants/apiUrls';

import { Storage } from 'way2go-common';

class Logo extends React.Component {
    constructor(props) {
        super(props);

        this.state = { logo: '' };

    }

    componentWillMount() {
        axios.post(ApiUrls.productservice + "/product/getproductlogo", {
            "orgId": Storage().getValue(Storage().options.ORG_ID).toString()
        }).then((response) => {
            if (response.data.outputStatus == "success") {
                this.setState({ 'logo': response.data.data.path });
            }
        });
    }

    render() {
        return (
                <div>
                    {this.state.logo && <span> &nbsp; <img  className="logoStyle" src={this.state.logo} /></span>}
                </div>
        );
    }
}

export default Logo;
