import axios from 'axios';
import {Storage} from 'way2go-common';

import * as constants from '../Constants/apiUrls';

class ChangePasswordApi {

  static changePassword(user) {
    const data={
        "key": Storage().getValue(Storage().options.AUTH_TOKEN), 
        'password': user.oldPassword,
        'newPassword': user.newPassword
    }
    const reqObj = {
        method: 'POST',
        url: constants.authservice+ '/auth/changePassword',
        // url:"https://c4tneplantest.azure-api.net/authentication/auth/changePassword",
        data
      };
    return axios(reqObj);
  }
}

export default ChangePasswordApi;

