svg.script:
        // 

        // Check if local storage is supported
        function isLocalStorageSupported() {
            try {
                var test = localStorage;

                return true;
            } catch (e) {
                return false;
            }
        }

        function getCookie(cname) {
            var name = cname + "=";
            var decodedCookie = decodeURIComponent(document.cookie);
            var ca = decodedCookie.split(';');
            for(var i = 0; i <ca.length; i++) {
                var c = ca[i];
                while (c.charAt(0) == ' ') {
                    c = c.substring(1);
                }
                if (c.indexOf(name) == 0) {
                    return c.substring(name.length, c.length);
                }
            }
            return "";
        }

        function getValue(option) {
            if(this.isLocalStorageSupported()) {
                return localStorage.getItem(option) ? JSON.parse(localStorage.getItem(option)) : 'blue';
            } else {
                return getCookie(option) ? JSON.parse(getCookie(option)) : 'blue';
            }
        }

        var shade = getValue('primary');

        var colorIndex = {
            'red': '#F44336',
            'pink': '#E91E63',
            'purple': '#9C27B0',
            'deep-purple': '#673AB7',
            'indigo': '#3F51B5',
            'blue': '#2196F3',
            'light-blue': '#03A9F4',
            'cyan': '#00BCD4',
            'teal': '#009688',
            'green': '#4CAF50',
            'light-green': '#8BC34A',
            'lime': '#CDDC39',
            'yellow': '#FFEB3B',
            'amber': '#FFC107',
            'orange': '#FF9800',
            'deep-orange': '#FF5722',
            'brown': '#795548',
            'grey': '#9E9E9E',
            'blue-grey': '#607D8B'
        }

        var color = colorIndex[shade];

        document.getElementsByTagName('stop')[0].setAttribute('stop-color', color);
        document.getElementsByTagName('circle')[0].setAttribute('stroke', color);

        var elem = document.getElementsByTagName('stop')[0];

        // t: current time, b: begInnIng value, c: change In value, d: duration

        var d = 4000;
        var t = 0;
        var c = 1;
        var b = 0;

        var id = setInterval(frame, 10);
        function frame() {
            if (t == 4000) {
                t = 0;
            } else {
                t = t + 10;
                elem.setAttribute('offset', -c/2 * (Math.cos(Math.PI*t/d) - 1) + b);
            }
        }

