import Utils from '../Utils/Utils';
import Authorisation from '../Authorisation/Authorisation';

class RedirectFunctions {
  static getAfterLoginUrl() {
    let returnUrl = Utils.getParameterByName('returnUrl');

    if(returnUrl) {
      return returnUrl;
    } else if(Authorisation.hasPermissionWithKey('tmc_access')) {
      return '/sdnodeedge';
    } else if(Authorisation.hasPermissionWithKey('cubepage_access')) {
      return '/org/adminlanding';
    } else {
      return '/';
    }
  }

  static getHomeUrl() {
    if(Authorisation.hasPermissionWithKey('tmc_access')) {
      return '/sdnodeedge';
    } else {
      return '/';
    }
  }
}

export default RedirectFunctions;
