import * as colors from 'material-ui/styles/colors';

const ThemePalettes = (color) => {

    let colorName = color.replace(/-([a-z])/g, function (g) {
      return g[1].toUpperCase();
    }); //color name in camel case

    switch (color) {

      default:
        return {
                palette: {
                    primary1Color: colors[colorName + '500'],
                    primary2Color: colors[colorName + '700'],
                    primary3Color: colors.grey600,
                    primaryDarkColor: colors[colorName + '800'] || colors['cyan800'],
                    primaryLightColor1: colors[colorName + '50'],
                    primaryLightColor2: colors[colorName + '100'],
                    alternateTextColor: '#FFF',
                    accent3Color: colors['grey700']
                }
            };
    }
}

export default ThemePalettes;
