import React from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';

import LoadingIconComponent from './LoadingIconComponent/LoadingIconComponent.js';
import AlertService from './AlertService/AlertService.js';

class App extends React.Component {

    constructor(props) {
      super(props);

      this.state = {};

      this.updateAlertMessage = this.updateAlertMessage.bind(this);
    }

    updateAlertMessage(message) {
      this.setState({message: message});
    }
    render() {
        return (
          <MuiThemeProvider>
            <div>
                <div>

                </div><LoadingIconComponent show={true} size={'big'}/><AlertService message={this.state.message}/>
                <div style={{height:"80%"}}></div>
                <div>

                </div>  
            </div>
          </MuiThemeProvider>
        );
    }
}

export default App;
